	if(function_exists('submitlog_before_delete')){
		$args=array();
		if(!submitlog_before_delete($selected_id, $skipChecks, getMemberInfo(), $args))
			return $Translation['Couldn\'t delete this record'];
	}

	sql("delete from `submitlog` where `submid`='$selected_id'", $eo);

