function usrreviews_insert(){
	global $Translation;

	// mm: can member insert record?
	$arrPerm=getTablePermissions('usrreviews');
	if(!$arrPerm[1]){
		return false;
	}

	$data['progid'] = makeSafe($_REQUEST['progid']);
		if($data['progid'] == empty_lookup_value){ $data['progid'] = ''; }
	$data['useripaddr'] = makeSafe($_REQUEST['useripaddr']);
		if($data['useripaddr'] == empty_lookup_value){ $data['useripaddr'] = ''; }
	$data['revtime'] = makeSafe($_REQUEST['revtime']);
		if($data['revtime'] == empty_lookup_value){ $data['revtime'] = ''; }
	$data['username'] = makeSafe($_REQUEST['username']);
		if($data['username'] == empty_lookup_value){ $data['username'] = ''; }
	$data['userrating'] = makeSafe($_REQUEST['userrating']);
		if($data['userrating'] == empty_lookup_value){ $data['userrating'] = ''; }
	$data['usercomment'] = makeSafe($_REQUEST['usercomment']);
		if($data['usercomment'] == empty_lookup_value){ $data['usercomment'] = ''; }
	if($data['progid'] == '') $data['progid'] = "0";
	if($data['userrating'] == '') $data['userrating'] = "0";

	// hook: usrreviews_before_insert
	if(function_exists('usrreviews_before_insert')){
		$args=array();
		if(!usrreviews_before_insert($data, getMemberInfo(), $args)){ return false; }
	}

	$o=array('silentErrors' => true);
	sql('insert into `usrreviews` set       `progid`=' . (($data['progid'] !== '' && $data['progid'] !== NULL) ? "'{$data['progid']}'" : 'NULL') . ', `useripaddr`=' . (($data['useripaddr'] !== '' && $data['useripaddr'] !== NULL) ? "'{$data['useripaddr']}'" : 'NULL') . ', `revtime`=' . (($data['revtime'] !== '' && $data['revtime'] !== NULL) ? "'{$data['revtime']}'" : 'NULL') . ', `username`=' . (($data['username'] !== '' && $data['username'] !== NULL) ? "'{$data['username']}'" : 'NULL') . ', `userrating`=' . (($data['userrating'] !== '' && $data['userrating'] !== NULL) ? "'{$data['userrating']}'" : 'NULL') . ', `usercomment`=' . (($data['usercomment'] !== '' && $data['usercomment'] !== NULL) ? "'{$data['usercomment']}'" : 'NULL'), $o);
	if($o['error']!=''){
		echo $o['error'];
		echo "<a href=\"usrreviews_view.php?addNew_x=1\">{$Translation['< back']}</a>";
		exit;
	}

	$recID = db_insert_id(db_link());

	// hook: usrreviews_after_insert
	if(function_exists('usrreviews_after_insert')){
		$res = sql("select * from `usrreviews` where `revid`='" . makeSafe($recID, false) . "' limit 1", $eo);
		if($row = db_fetch_assoc($res)){
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args=array();
		if(!usrreviews_after_insert($data, getMemberInfo(), $args)){ return (get_magic_quotes_gpc() ? stripslashes($recID) : $recID); }
	}

	// mm: save ownership data
	sql("insert ignore into membership_userrecords set tableName='usrreviews', pkValue='$recID', memberID='".getLoggedMemberID()."', dateAdded='".time()."', dateUpdated='".time()."', groupID='".getLoggedGroupID()."'", $eo);

	return (get_magic_quotes_gpc() ? stripslashes($recID) : $recID);
}

function usrreviews_delete($selected_id, $AllowDeleteOfParents=false, $skipChecks=false){
	// insure referential integrity ...
	global $Translation;
	$selected_id=makeSafe($selected_id);

	// mm: can member delete record?
	$arrPerm=getTablePermissions('usrreviews');
	$ownerGroupID=sqlValue("select groupID from membership_userrecords where tableName='usrreviews' and pkValue='$selected_id'");
	$ownerMemberID=sqlValue("select lcase(memberID) from membership_userrecords where tableName='usrreviews' and pkValue='$selected_id'");
	if(($arrPerm[4]==1 && $ownerMemberID==getLoggedMemberID()) || ($arrPerm[4]==2 && $ownerGroupID==getLoggedGroupID()) || $arrPerm[4]==3){ // allow delete?
		// delete allowed, so continue ...
	}else{
		return $Translation['You don\'t have enough permissions to delete this record'];
	}

	// hook: usrreviews_before_delete
	if(function_exists('usrreviews_before_delete')){
		$args=array();
		if(!usrreviews_before_delete($selected_id, $skipChecks, getMemberInfo(), $args))
			return $Translation['Couldn\'t delete this record'];
	}

	sql("delete from `usrreviews` where `revid`='$selected_id'", $eo);

	// hook: usrreviews_after_delete
	if(function_exists('usrreviews_after_delete')){
		$args=array();
		usrreviews_after_delete($selected_id, getMemberInfo(), $args);
	}

	// mm: delete ownership data
	sql("delete from membership_userrecords where tableName='usrreviews' and pkValue='$selected_id'", $eo);
}

