		exit;
	}


	// hook: usrreviews_after_update
	if(function_exists('usrreviews_after_update')){
		$res = sql("SELECT * FROM `usrreviews` WHERE `revid`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)){
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = $data['revid'];
		$args = array();
		if(!usrreviews_after_update($data, getMemberInfo(), $args)){ return; }
	}

	// mm: update ownership data
	sql("update membership_userrecords set dateUpdated='".time()."' where tableName='usrreviews' and pkValue='".makeSafe($selected_id)."'", $eo);

